// src/pages/Home.js
import React from 'react';
import News from '../components/News';
import Blogs from '../components/Blogs';
import './Home.css';

      const Home = () => {
        const [cars, setCars] = useState([]);
        const [searchResults, setSearchResults] = useState([]);
        const [searchPerformed, setSearchPerformed] = useState(false);
      
        useEffect(() => {
          fetchCars();
        }, []);
      
        const fetchCars = async () => {
          try {
            const res = await axios.get('http://localhost:5000/api/cars');
            setCars(res.data);
          } catch (err) {
            console.error(err);
          }
        };
      
        const handleSearch = async (query) => {
          if (!query.trim()) {
            setSearchResults([]);
            setSearchPerformed(false);
            return;
          }
      
          try {
            const res = await axios.get('http://localhost:5000/api/cars', {
              params: { search: query }
            });
            setSearchResults(res.data);
            setSearchPerformed(true);
          } catch (err) {
            console.error(err);
          }
        };
      
        return (
          <div>
            <h1>Find Your Dream Car!</h1>
            <SearchBar onSearch={handleSearch} />
      
            {searchPerformed ? (
              <div>
                <h2>Search Results</h2>
                {searchResults.length > 0 ? (
                  <CarList cars={searchResults} />
                ) : (
                  <p>No cars found matching your search.</p>
                )}
              </div>
            ) : (
              <>
                <p>Your one-stop destination for comparing cars, staying updated with the latest automotive news, and reading insightful blogs.</p>
                <News />
                <Blogs />
                <div>
                  <h2>All Cars</h2>
                  <CarList cars={cars} />
                </div>
              </>
            )}
          </div>
        );
      };
      
      const CarList = ({ cars }) => {
        return (
          <div className="car-list">
            {cars.map((car) => (
              <div key={car._id} style={styles.carCard}>
                <img src={car.imageUrl} alt={`${car.make} ${car.model}`} style={styles.image} />
                <h3>{car.make} {car.model} ({car.year})</h3>
                <p>Price: ${car.specifications.price}</p>
                {/* Add more details or a link to the car detail page */}
              </div>
            ))}
          </div>
        );
      };
      
      const styles = {
        carCard: {
          border: '1px solid #ccc',
          borderRadius: '8px',
          padding: '1em',
          margin: '1em',
          width: '250px',
          textAlign: 'center',
          boxShadow: '2px 2px 12px #aaa',
        },
        image: {
          width: '100%',
          height: '150px',
          objectFit: 'cover',
          borderRadius: '4px',
        },
      };
      
      export default Home;
