// src/pages/Home.js

import React, { useState, useEffect } from 'react';
import News from '../components/News';
import Blogs from '../components/Blogs';
import SearchBar from '../components/SearchBar';
import './Home.css';
import axios from 'axios';

const Home = () => {
  const [cars, setCars] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [searchPerformed, setSearchPerformed] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchCars();
  }, []);

  const fetchCars = async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await axios.get('http://localhost:5000/api/cars');
      setCars(res.data);
    } catch (err) {
      console.error(err);
      setError('Failed to fetch cars.');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (query) => {
    if (!query.trim()) {
      setSearchResults([]);
      setSearchPerformed(false);
      return;
    }

    setLoading(true);
    setError(null);
    try {
      const res = await axios.get('http://localhost:5000/api/cars', {
        params: { search: query }
      });
      setSearchResults(res.data);
      setSearchPerformed(true);
    } catch (err) {
      console.error(err);
      setError('Failed to perform search.');
    } finally {
      setLoading(false);
    }
  };

  const handleClearSearch = () => {
    setSearchResults([]);
    setSearchPerformed(false);
    setError(null);
  };

  return (
    <div className="content">
      <h1>Find Your Dream Car!</h1>
      <SearchBar onSearch={handleSearch} />

      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}

      {searchPerformed ? (
        <div>
          <h2>Search Results</h2>
          <button onClick={handleClearSearch} className="clear-button">Clear Search</button>
          {searchResults.length > 0 ? (
            <CarList cars={searchResults} />
          ) : (
            <p>No cars found matching your search.</p>
          )}
        </div>
      ) : (
        <>
          <p>Your one-stop destination for comparing cars, staying updated with the latest automotive news, and reading insightful blogs.</p>
          <News />
          <Blogs />
          <div>
            <h2>All Cars</h2>
            {loading ? (
              <p>Loading cars...</p>
            ) : cars.length > 0 ? (
              <CarList cars={cars} />
            ) : (
              <p>No cars available.</p>
            )}
          </div>
        </>
      )}
    </div>
  );
};

const CarList = ({ cars }) => {
  return (
    <div className="car-list">
      {cars.map((car) => (
        <div key={car._id} className="car-card">
          <img src={car.imageUrl} alt={`${car.make} ${car.model}`} className="car-image" />
          <h3>{car.make} {car.model} ({car.year})</h3>
          <p>Price: ${car.specifications.price}</p>
          {/* Add more details or a link to the car detail page */}
        </div>
      ))}
    </div>
  );
};

export default Home;
