import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const NewsDetail = () => {
  const { id } = useParams();
  const [newsItem, setNewsItem] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchNewsItem = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/news/${id}`);
        setNewsItem(res.data);
      } catch (err) {
        setError('Failed to fetch news item.');
      } finally {
        setLoading(false);
      }
    };

    fetchNewsItem();
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p className="error">{error}</p>;
  if (!newsItem) return <p>No news item found.</p>; // Handle case when newsItem is null

  return (
    <div className="news-detail">
      <h2>{newsItem.title}</h2>
      <img src={newsItem.imageUrl} alt={newsItem.title} />
      <p>{newsItem.description}</p>
      <p>{newsItem.body}</p>
      <p>Published at: {new Date(newsItem.publishedAt).toLocaleString()}</p>
    </div>
  );
};

export default NewsDetail;
