// src/pages/Home.js
import React from 'react';
import News from '../components/News';
import Blogs from '../components/Blogs';
import './Home.css';

const Home = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  // Handle search input changes
  const handleSearchInput = (event) => {
    setSearchTerm(event.target.value);
  };

  // Handle form submission for search
  const handleSearchSubmit = (event) => {
    event.preventDefault();
    fetchCars(searchTerm);
  };

  // Fetch car data from API based on search term
  const fetchCars = async (term) => {
    try {
      const response = await fetch(`https://example-car-api.com/search?query=${term}`); // Use actual API
      const data = await response.json();
      setSearchResults(data.cars || []); // Assuming the API returns an array of car objects
    } catch (error) {
      console.error('Error fetching cars:', error);
    }
  };

  return (
    <div>
      <h1>Find Your Dream Car!</h1>

      {/* Search Bar */}
      <form onSubmit={handleSearchSubmit}>
        <input
          type="text"
          placeholder="Search cars..."
          value={searchTerm}
          onChange={handleSearchInput}
        />
        <button type="submit">Search</button>
      </form>

      {/* Display search results */}
      <div className="search-results">
        {searchResults.length > 0 ? (
          <ul>
            {searchResults.map((car, index) => (
              <li key={index}>
                <h3>{car.name}</h3>
                <p>{car.description}</p>
                <p>Price: {car.price}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No cars found. Try searching for a specific model or make.</p>
        )}
      </div>

      <p>Your one-stop destination for comparing cars, staying updated with the latest automotive news, and reading insightful blogs.</p>

      {/* News and Blogs components */}
      <News />
      <Blogs />
    </div>
  );
};

export default Home;
