import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Import for navigation
import News from '../components/News';
import Blogs from '../components/Blogs';
import SearchBar from '../components/SearchBar';
import './Home.css';
import axios from 'axios';

const Home = () => {
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate(); // Use navigate hook

  useEffect(() => {
    fetchCars();
  }, []);

  const fetchCars = async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await axios.get('http://localhost:5000/api/cars');
      setCars(res.data);
    } catch (err) {
      console.error(err);
      setError('Failed to fetch cars.');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (query) => {
    if (!query.trim()) {
      return;
    }

    setLoading(true);
    setError(null);
    try {
      const res = await axios.get('http://localhost:5000/api/cars', {
        params: { search: query }
      });
      
      if (res.data.length > 0) {
        // Navigate to the search results page with the results as state
        navigate('/search-results', { state: { searchResults: res.data } });
      } else {
        setError('No cars found matching your search.');
      }
    } catch (err) {
      console.error(err);
      setError('No cars found matching your search.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="home-container">
      <h1>Find Your Dream Car!</h1>
      <div className="search-bar-container">
        <SearchBar onSearch={handleSearch} />
      </div>

      {loading && <p className="loading">Loading...</p>}
      {error && <p className="error">{error}</p>}

      <p>Your one-stop destination for comparing cars, staying updated with the latest automotive news, and reading insightful blogs.</p>
      <News />
      <Blogs />

      <div>
        <h2>All Cars</h2>
        {loading ? (
          <p className="loading">Loading cars...</p>
        ) : cars.length > 0 ? (
          <CarList cars={cars} />
        ) : (
          <p>No cars available.</p>
        )}
      </div>
    </div>
  );
};

const CarList = ({ cars }) => {
  return (
    <div className="car-list">
      {cars.map((car) => (
        <div key={car._id} className="car-card">
          <img src={car.imageUrl} alt={`${car.make} ${car.model}`} className="car-image" />
          <h3>{car.make} {car.model} ({car.year})</h3>
          <p>Price: ${car.specifications.price}</p>
        </div>
      ))}
    </div>
  );
};

export default Home;
